import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import { useState } from "react";
function Expenses({ expenses, filter }) {
  const [filteredYear, setFilteredYear] = useState(2020);
  const filterHandler = (YearValue) => {
    setFilteredYear(YearValue);
    filter(YearValue);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onFilter={filterHandler} dropdownYear={filteredYear} />

        {expenses.length === 0 ? (
          <p>No expenses found</p>
        ) : (
          expenses.map((expense) => {
            const { id, date, title, amount } = expense;
            return (
              <ExpenseItem key={id} date={date} title={title} amount={amount} />
            );
          })
        )}
      </Card>
    </div>
  );
}

export default Expenses;
