import React, { useEffect, useState, useCallback } from "react";

import Tasks from "./components/Tasks/Tasks";
import NewTask from "./components/NewTask/NewTask";
import useFetch from "./Hooks/use-fetch";
function App() {
  const [tasks, setTasks] = useState([]);
  const transformTaskes = useCallback((tasksObj) => {
    const loadedTaskes = [];
    for (const taskKey in tasksObj) {
      loadedTaskes.push({ id: taskKey, text: tasksObj[taskKey].text });
    }
    setTasks(loadedTaskes);
  }, []);
  const {
    isLoading,
    error,
    sendRequest: fetchTasks,
  } = useFetch(
    {
      url: "https://bd-project-12ea0-default-rtdb.europe-west1.firebasedatabase.app/tasks.json",
    },
    transformTaskes
  );

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks]);

  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };

  return (
    <React.Fragment>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks
        items={tasks}
        loading={isLoading}
        error={error}
        onFetch={fetchTasks}
      />
    </React.Fragment>
  );
}

export default App;
