import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [nameWasTouched, setNameWasTouched] = useState(false);
  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailWasTouched, setEmailWasTouched] = useState(false);
  const nameisValid = enteredName.trim() !== "";
  const emailisValid = enteredEmail.trim() !== "";
  const nameInputIsInvalid = !nameisValid && nameWasTouched;

  let formIsValid = false;
  if (nameisValid) {
    formIsValid = true;
  }
  const nameInputChangeHandler = (e) => {
    setEnteredName(e.target.value);
  };

  const nameInputBlurHandler = (e) => {
    setNameWasTouched(true);
  };

  const formSubmit = (e) => {
    e.preventDefault();
    setNameWasTouched(true);
    if (!nameisValid) {
      return;
    }
    setEnteredName("");
    setNameWasTouched(false);
  };
  return (
    <form onSubmit={formSubmit}>
      <div
        className={`${
          nameInputIsInvalid ? "form-control invalid" : "form-control"
        }`}
      >
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Input is not Valid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
