import { useState } from "react";
import { useInput } from "../hooks/use-input";
const SimpleInput = (props) => {
  const {
    value: enteredName,
    hasError: nameInputIsInvalid,
    valueIsValid: nameIsValid,
    valueInputChangeHandler: nameInputChangeHandler,
    valueInputBlurHandler: nameInputBlurHandler,
  } = useInput((value) => value.trim() !== "");

  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailWasTouched, setEmailWasTouched] = useState(false);

  const emailIsValid =
    enteredEmail.trim() !== "" &&
    enteredEmail.indexOf("@") !== -1 &&
    enteredEmail.indexOf(".") !== -1;
  const emailInputIsInvalid = !emailIsValid && emailWasTouched;

  let formIsValid = false;
  if (nameIsValid && emailIsValid) {
    formIsValid = true;
  }

  const emailInputChangeHandler = (e) => {
    setEnteredEmail(e.target.value);
  };

  const emailInputBlurHandler = (e) => {
    setEmailWasTouched(true);
  };
  const formSubmit = (e) => {
    e.preventDefault();
    setEmailWasTouched(true);
    if (!nameIsValid || !emailIsValid) {
      return;
    }
    setEnteredEmail("");
    setNameWasTouched(false);
  };
  return (
    <form onSubmit={formSubmit}>
      <div
        className={`${
          nameInputIsInvalid ? "form-control invalid" : "form-control"
        }`}
      >
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Input is not Valid</p>}
      </div>
      <div
        className={`${
          emailInputIsInvalid ? "form-control invalid" : "form-control"
        }`}
      >
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInputIsInvalid && (
          <p className="error-text">Input is not Valid</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
