import { createStore } from "redux";
import { createSlice } from "@reduxjs/toolkit";

createSlice({
  name: "counter",
  initialState: { counter: 0, showCounter: false },
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter += action.value;
    },
    showCounter() {},
  },
});
const storeReducer = (state = { counter: 0, showCounter: false }, action) => {
  if (action.type === "increment") {
    return { counter: state.counter + 1, showCounter: true };
  }
  if (action.type === "decrement") {
    return { counter: state.counter - 1, showCounter: true };
  }
  if (action.type === "increase") {
    return { counter: state.counter + action.value, showCounter: true };
  }
  if (action.type === "showCounter") {
    return { counter: state.counter, showCounter: !state.showCounter };
  }
  return state;
};

const store = createStore(storeReducer);

export default store;
